(function($, Drupal, drupalSettings) {
  Drupal.behaviors.bookmark = {
    attach: async function(context, settings) {
      const url = window.location.protocol + "//" + window.location.hostname + '/api/v1/meconnect/bookmark';
      let bookmarkButtons = $('.bookmark-link');
      let relyingParty = drupalSettings.meconnect_relying_party;
      //@step1: check if user is logged in or not
      if (ssoMeConnect.isAuthenticate()) {
        //@step2: check if localStorage is set with a value
        if (ssoMeConnect.getlocalStorage("meconnectBookmarks")) {
          let currentBookmarksList = ssoMeConnect.getlocalStorage("meconnectBookmarks");
          if (bookmarkButtons) {
            bookmarkButtons.once().each(function() {
              let element = $(this);
              $(this).attr('data-isloggedIn', true);
              bookmarkButtons.prop('disabled');
              var uuid = $(this).data('uuid');
              var multipleDupUuid = $('[data-uuid="'+ uuid + '"]');
              let isBookmarked = currentBookmarksList.includes(uuid);
              multipleDupUuid.each(function( index ) {
                element = $(this);
                meConnectBookarkToggle(isBookmarked, element);
              });
            });
          }
        }
        else {
          let bookmarksList = await ssoMeConnect.Request("Get", url);
          let bookmarksUuids = [];
          if (bookmarksList) {
            bookmarksList.forEach(element => {
              bookmarksUuids.push(element.uuid);
            });
          }
          ssoMeConnect.setlocalStorage("meconnectBookmarks", bookmarksUuids);
          let currentBookmarksList = ssoMeConnect.getlocalStorage("meconnectBookmarks");
          bookmarkButtons.once().each(function() {
            let element = $(this);
            $(this).attr('data-isloggedIn', true);
            bookmarkButtons.prop('disabled');
            var uuid = $(this).data('uuid');
            var multipleDupUuid = $('[data-uuid="'+ uuid + '"]');
            let isBookmarked = currentBookmarksList.includes(uuid);
            multipleDupUuid.each(function( index ) {
              element = $(this);
              meConnectBookarkToggle(isBookmarked, element);
            });
          });
        }
      }
      else {
        if (ssoMeConnect.getlocalStorage("meconnectBookmarks")) {
          ssoMeConnect.deletelocalStorage("meconnectBookmarks");
        }
        bookmarkButtons.once().each(async function() {
          $(this).addClass('hidden');
          $(this).attr('data-isloggedIn', false);
          $(this).attr('data-isbookmarked', false);
          $(this).parent().find('.bookmark-link-anonymous').removeClass('hidden');
        });
      }

      bookmarkButtons.once('onClick').click(async function() {
        var element = $(this);
        var uuid = $(this).data('uuid');
        var nid = $(this).attr('data-nid');
        var dataElement = $('a[data-nid = ' + nid + ']');
        var multipleDupUuid = $('[data-uuid="'+ uuid + '"]');
        const isBookmarked = $(this).attr('data-isbookmarked');
        var data = {};
        element.parent().find('.ajax-progress-throbber').show();

        // disabling link for 2 second on click.
        if (element.attr('data-isbookmarked') === 'false') {
          element.addClass('is-disabled');
          setTimeout(function() {
            element.removeClass('is-disabled');
          }, 2000);
        }

        data[relyingParty] = {
          bookmarks: [{
            uuid: uuid,
          }]
        };

        if (isBookmarked === 'true') {
          var action = 'unsubscribe';
          data[relyingParty].bookmarks[0].isDeleted = true;
        }
        else {
          var action = 'subscribe';
          data[relyingParty].bookmarks[0].entity_id = $(this).data('nid');
          data[relyingParty].bookmarks[0].path = window.location.pathname;
        }

        var update = await ssoMeConnect.userInfoUpdateApi(action, url, data);
        if (update.status === true) {
          if (update.action === 'subscribe') {
            ssoMeConnect.updatelocalStorge("meconnectBookmarks", "addItem", uuid);
            dataElement.find('.link__text').text('Bookmarked');
            dataElement.attr('data-isbookmarked', true);
            dataElement.addClass('active');
            setTimeout(function () {
              dataElement.removeClass('is-disabled');
            }, 2000);
          }
          else{
            ssoMeConnect.updatelocalStorge("meconnectBookmarks", "removeItem", uuid);
            dataElement.find('.link__text').text('Bookmarked');
            dataElement.attr('data-isbookmarked', false);
            dataElement.removeClass('active');
          }
          bookmarkButtons.parent().find('.ajax-progress-throbber').hide();
          const feedback_popup = $('.message-popup');
          feedback_popup.removeClass('hidden');
          if (update.action !== 'subscribe') {
            feedback_popup.addClass('message-popup__unbookmark');
          } else {
            feedback_popup.removeClass('message-popup__unbookmark');
          }
          feedback_popup.find('.message-popup__content').html(function() {
            return update.action === 'subscribe' ? `<svg class="link__icon link__icon--"><use xlink:href="/themes/custom/mc_todayonline_theme/dist/icons.svg#social-filled-bookmark"></use></svg>
            <span class="link__text">Added to your bookmarks.</span>`
              : `<svg class="link__icon link__icon--"><use xlink:href="/profiles/custom/mediacorp/themes/mc_core_theme/dist/icons.svg#social-filled-bookmark"></use></svg>
            <span class="link__text">Removed from your bookmarks.</span>`
          });
          feedback_popup.find('.close__icon').once('message-popup-close').click(function () {
            feedback_popup.addClass('hidden');
          });
          // Hide after 3 seconds.
          setTimeout(function() {
            feedback_popup.addClass('hidden').removeClass('message-popup__unbookmark');
          }, 3000);
        }
        else {
          console.log(update);
        }

        //Track bookmark click
        const pageUrlPath = element.siblings('.trigger-popup--share').attr('data-link');
        const bookmarkData = { clickname: 'Click on Bookmark', 'path': pageUrlPath };
        _adobeUtility.adobeAnalyticsTrack('click', bookmarkData);
      });

      //Function used to toggle bookmark button.
      function meConnectBookarkToggle(active, element) {
        if (active) {
          element.find('.link__text').text('Bookmarked');
          element.attr('data-isbookmarked', true);
          element.addClass('active');
          setTimeout(function() {
            element.removeClass('is-disabled');
          }, 2000);
        }
        else {
          element.find('.link__text').text('Bookmark');
          element.attr('data-isbookmarked', false);
          element.removeClass('active');
        }
      }

    }//attach ends here
  };
})(jQuery, Drupal, drupalSettings);
